import React,{useState,useCallback}from"react";const useHttps=()=>{const[isLoading,setIsLoading]=useState(!1),[fetchError,setFetchError]=useState(null),fetchMovies=useCallback(async(requestConfig,applyData)=>{try{setIsLoading(!0),setFetchError(null);const response=await fetch(requestConfig.url,{method:requestConfig.method?requestConfig.method:"GET",headers:requestConfig.headers?requestConfig.headers:{},body:requestConfig.body?JSON.stringify(requestConfig.body):null});if(!response.ok)throw new Error("Something went wrong!");const data=await response.json();applyData(data)}catch(err){setFetchError(err.message),setIsLoading(!1)}},[]);return{isLoading:isLoading,fetchError:fetchError,fetchMovies:fetchMovies}};export default useHttps;